function Modal({ onClose }) {
    const modalStyle = {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
    };
    
    const modalContentStyle = {
        backgroundColor: 'white',
        padding: '20px',
        borderRadius: '5px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
        width: '60%', // 네모의 너비를 화면의 60%로 설정
        height: '70%', // 네모의 높이를 화면의 70%로 설정
        position: 'relative', // 닫기 버튼을 위한 상대 위치 설정
    };
    
    const closeButtonStyle = {
        position: 'absolute',
        top: '10px',
        right: '20px',
        background: 'none',
        border: 'none',
        fontSize: '20px',
        cursor: 'pointer',
    };
    
    return (
        <div style={modalStyle}>
            <div style={modalContentStyle}>
            <button style={closeButtonStyle} onClick={onClose}>
                <img src="/css/image/ico_allmenu_close.png" alt="Close" />
            </button>
            <button type="button" class="btn mobile btnAllMenuM" title="전체메뉴 닫힘">전체메뉴</button>
            <p style={{textAlign: 'center'}}>여기에 개인정보처리방침 내용을 넣습니다.</p>
            </div>
        </div>
    );
}

export default Modal;
