import React, { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';

import * as EgovNet from 'api/egovFetch';
import URL from 'constants/url';
import CODE from 'constants/code';

import { default as EgovLeftNav } from 'components/leftmenu/EgovLeftNavLicense';
import EgovAttachFile from 'components/EgovAttachFile';

function EgovLicenseScheduleDetail(props) {
    console.group("EgovLicenseScheduleDetail");
    console.log("[Start] EgovLicenseScheduleDetail ------------------------------");
    console.log("EgovLicenseScheduleDetail [props] : ", props);

    const navigate = useNavigate();
    const location = useLocation();
    console.log("EgovLicenseScheduleDetail [location] : ", location);

    const [licenseDetail, setlicenseDetail] = useState({});
    const [boardAttachFiles, setBoardAttachFiles] = useState();
    const [user, setUser] = useState({});

    const retrieveDetail = () => {

        const retrieveDetailURL = `/schedule/${location.state?.lcnsId}`;

        const requestOptions = {
            method: "GET",
            headers: {
                'Content-type': 'application/json',
            }
        }
        EgovNet.requestFetch(retrieveDetailURL,
            requestOptions,
            function (resp) {
                let rawlicenseDetail = resp.result.licenseDetail;
                rawlicenseDetail.startDateTime = convertDate(rawlicenseDetail.lcnsBgnde);
                rawlicenseDetail.endDateTime = convertDate(rawlicenseDetail.lcnsEndde);
                rawlicenseDetail.reptitSeCodeNm = getCodeName(resp.result.reptitSeCode, resp.result.licenseDetail.reptitSeCode);
                rawlicenseDetail.lcnsIpcrCodeNm = getCodeName(resp.result.lcnsIpcrCode, resp.result.licenseDetail.lcnsIpcrCode);
                rawlicenseDetail.lcnsSeNm = getCodeName(resp.result.lcnsSe, resp.result.licenseDetail.lcnsSe);
                setlicenseDetail(rawlicenseDetail);
                setUser(resp.result.user);
                setBoardAttachFiles(resp.result.resultFiles);
            }
        );
    }
    const convertDate = (str) => {
        let year = str.substring(0, 4);
        let month = str.substring(4, 6);
        let date = str.substring(6, 8);
        let hour = str.substring(8, 10);
        let minute = str.substring(10, 12);
        return {
            year: year,
            month: month,
            date: date,
            hour: hour,
            minute: minute,
            dateForm: year + "년 " + month + "월 " + date + "일 " + hour + "시 " + minute + "분 "
        }
    }

    const getCodeName = (codeArr, code) => {
        return (
            codeArr.map((codeObj) => {
                if (codeObj.code === code.trim()) return codeObj.codeNm
                else return "";
            })
        );
    };

    const onClickDeletelicense = (lcnsId) => {
        const deleteBoardURL = `/schedule/${lcnsId}`;

        const requestOptions = {
            method: "DELETE",
            headers: {
                'Content-type': 'application/json',
            }
        }

        EgovNet.requestFetch(deleteBoardURL,
            requestOptions,
            (resp) => {
                console.log("====>>> lcnse delete= ", resp);
                if (Number(resp.resultCode) === Number(CODE.RCV_SUCCESS)) {
                    alert("게시글이 삭제되었습니다.")
                    navigate(URL.LICENSE_SCHEDULE ,{ replace: true });
                } else {
                    // alert("ERR : " + resp.message);
                    navigate({pathname: URL.ERROR}, {state: {msg : resp.resultMessage}});
                }

            }
        );
    }

    useEffect(function () {
        retrieveDetail();
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    console.log("------------------------------EgovLicenseScheduleDetail [End]");
    console.groupEnd("EgovLicenseScheduleDetail");
    return (
        <div className="container">
            <div className="c_wrap">
                {/* <!-- Location --> */}
                <div className="location">
                    <ul>
                        <li><Link to={URL.MAIN} className="home">Home</Link></li>
                        <li><Link to={URL.LICENSE}>운전면허시험</Link></li>
                        <li>운전면허시험 접수 확인</li>
                    </ul>
                </div>
                {/* <!--// Location --> */}

                <div className="layout">
                    {/* <!-- Navigation --> */}
                    <EgovLeftNav></EgovLeftNav>
                    {/* <!--// Navigation --> */}

                    <div className="contents SITE_GALLARY_VIEW" id="contents">
                        {/* <!-- 본문 --> */}

                        <div className="top_tit">
                            <h1 className="tit_1">운전면허시험</h1>
                        </div>

                        <h2 className="tit_2">운전면허시험 접수 확인</h2>

                        {/* <!-- 게시판 상세보기 --> */}
                        <div className="board_view2">
                            <dl>
                                <dt>일정구분</dt>
                                <dd>{licenseDetail.lcnsSeNm}</dd>
                            </dl>
                            <dl>
                                <dt>중요도</dt>
                                <dd>{licenseDetail.lcnsIpcrCodeNm}</dd>
                            </dl>
                            <dl>
                                <dt>부서</dt>
                                <dd>{licenseDetail.lcnsDeptName}</dd>
                            </dl>
                            <dl>
                                <dt>일정명</dt>
                                <dd>{licenseDetail.lcnsNm}</dd>
                            </dl>
                            <dl>
                                <dt>일정내용</dt>
                                <dd>{licenseDetail.lcnsCn}</dd>
                            </dl>
                            <dl>
                                <dt>반복구분</dt>
                                <dd>{licenseDetail.reptitSeCodeNm}</dd>
                            </dl>
                            <dl>
                                <dt>날짜/시간</dt>
                                <dd> {licenseDetail.startDateTime?.dateForm} ~ {licenseDetail.endDateTime?.dateForm}</dd>
                            </dl>
                            <dl>
                                <dt>담당자</dt>
                                <dd>{licenseDetail.lcnsChargerName}</dd>
                            </dl>

                            <EgovAttachFile boardFiles={boardAttachFiles} />

                            {/* <!-- 버튼영역 --> */}
                            <div className="board_btn_area">
                                {user.id &&
                                    <div className="left_col btn1">
                                        <Link to={{pathname: URL.LICENSE_SCHEDULE_MODIFY}}
                                            state={{
                                                lcnsId: location.state?.lcnsId
                                            }}
                                            className="btn btn_skyblue_h46 w_100">수정</Link>
                                        <button className="btn btn_skyblue_h46 w_100"
                                            onClick={(e) => {
                                                onClickDeletelicense(location.state?.lcnsId);
                                            }}>삭제</button>

                                    </div>
                                }
                                <div className="right_col btn1">
                                    <Link to={URL.LICENSE_SCHEDULE} className="btn btn_blue_h46 w_100">목록</Link>
                                </div>
                            </div>
                            {/* <!--// 버튼영역 --> */}
                        </div>
                        {/* <!-- 게시판 상세보기 --> */}

                        {/* <!--// 본문 --> */}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default EgovLicenseScheduleDetail;
