function Modal({ onClose }) {
    const modalStyle = {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
    };
    
    const modalContentStyle = {
        backgroundColor: 'white',
        padding: '20px',
        borderRadius: '5px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
        width: '60%',
        height: '70%',
        position: 'relative',
    };
    
    const closeButtonStyle = {
        position: 'absolute',
        top: '10px',
        right: '20px',
        background: 'none',
        border: 'none',
        fontSize: '20px',
        cursor: 'pointer',
    };
    
    return (
        <div style={modalStyle}>
            <div style={modalContentStyle}>
                <button style={closeButtonStyle} onClick={onClose}>X</button>
                <p style={{textAlign: 'center'}}>여기에 개인정보처리방침 내용을 넣습니다.</p>
            </div>
        </div>
    );
}

export default Modal;
