import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Modal from './Modal';

import * as EgovNet from 'api/egovFetch';
import URL from 'constants/url';
import CODE from 'constants/code';
import { default as EgovLeftNav } from 'components/leftmenu/EgovLeftNavAdmin';

function EgovAdminPasswordUpdate(props) {
    console.group("EgovAdminPasswordUpdate");
    console.log("[Start] EgovAdminPasswordUpdate ------------------------------");
    console.log("EgovAdminPasswordUpdate [props] : ", props);

    const navigate = useNavigate();
	const [oldPassword, setOldPassword] = useState('');
	const [newPassword, setNewPassword] = useState('');
	const formValidator = (formData) => {
        if (formData.get('old_password') === null || formData.get('old_password') === "") {
            alert("기존 암호는 필수 값입니다.");
            return false;
        }
        if (formData.get('new_password') === null || formData.get('new_password') === "") {
            alert("신규 암호는 필수 값입니다.");
            return false;
        }
        if (formData.get('new_password') === formData.get('old_password')) {
            alert("신규 암호는 기존 암호와 동일하게 사용할 수 없습니다.");
            return false;
        }
        return true;
    };

    const updateAdminPassword = () => {
		const editURL = "/adminPassword";

        let requestOptions ={};
        const formData = new FormData();
		formData.append("old_password", oldPassword);
		formData.append("new_password", newPassword);
        if (formValidator(formData)) {
            requestOptions = {
                method: "POST",
                headers: {
					'Content-type': 'application/json',

                },
                body: JSON.stringify({
	                "old_password": oldPassword,
	                "new_password": newPassword
	            })
            }
            EgovNet.requestFetch(editURL,
                requestOptions,
                (resp) => {
                    if (Number(resp.resultCode) === Number(CODE.RCV_SUCCESS)) {
						alert("OK 다음 로그인 시 신규 암호를 사용하세요.");
                        navigate({ pathname: URL.MAIN }, { replace: true });
                    } else {
						alert("Fail 변경되지 않았습니다. 다시 시도해 주세요.");
						navigate({pathname: URL.ERROR}, {state: {msg : resp.resultMessage}}); //에러메세지 변수명 변경
                    }
                }
            );
        };
    };

    useEffect(() => {
	// eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    console.log("------------------------------EgovAdminPasswordUpdate [End]");
    console.groupEnd("EgovAdminPasswordUpdate");

    const [isModalOpen, setIsModalOpen] = useState(false);

    const handlePrivacyPolicyClick = () => {
        const newWindow = window.open('', '', 'width=600,height=400');
        newWindow.document.write('<html><head><title>개인정보처리방침</title></head><body>');
        newWindow.document.write('<h1>개인정보처리방침</h1>');
        newWindow.document.write('<p>여기에 개인정보처리방침 내용을 넣습니다.</p>');
        newWindow.document.write('</body></html>');
        newWindow.document.close();
      };

    const handleCloseModal = () => {
        setIsModalOpen(false);
    };

    return (
        <div className="container">
            <div className="c_wrap">
                {/* <!-- Location --> */}
                <div className="location">
                    <ul>
                        <li><Link to={URL.MAIN} className="home">Home</Link></li>
                        <li>회원가입</li>
                    </ul>
                </div>
                {/* <!--// Location --> */}

                <div className="layout">

                    <div className="contents BOARD_CREATE_REG" id="contents">
                        {/* <!-- 본문 --> */}

                        <div className="top_tit">
                            <h1 className="tit_1">회원가입</h1>
                        </div>
                            <h2 className="tit_2"></h2>
                        <div className="board_view2">
                        <dl>
                            <dt><label htmlFor="terms">약관 동의</label><span className="req">필수</span></dt>
                            <dd>
                            <button onClick={handlePrivacyPolicyClick} style={{ marginRight: '5px' }}>개인정보처리방침</button>                            <input type="checkbox" name="terms" id="terms" required />
                            <label htmlFor="terms"> 이용 약관에 동의합니다.</label>
                            </dd>
                        </dl>
                        {/* 모달 컴포넌트를 조건부 렌더링 */}
                        {isModalOpen && <Modal onClose={handleCloseModal} />}

                        <dl>
                            <dt><label htmlFor="name">이름</label><span className="req">필수</span></dt>
                            <dd>
                            <input className="f_input2 w_full" type="text" name="name" id="name" required />
                            </dd>
                        </dl>
                        <dl>
                            <dt><label htmlFor="ssn1">주민등록번호</label><span className="req">필수</span></dt>
                            <dd>
                                <input className="f_input2" type="text" name="ssn1" id="ssn1" required maxLength="6"/>
                                -
                                <input className="f_input2" type="text" name="ssn2" required maxLength="7"/>
                            </dd>
                        </dl>
                        <dl>
                            <dt><label htmlFor="phone1">전화번호</label><span className="req">필수</span></dt>
                            <dd>
                                <input className="f_input2" type="tel" name="phone1" id="phone1" required maxLength="3" />
                                -
                                <input className="f_input2" type="tel" name="phone2" required maxLength="4" />
                                -
                                <input className="f_input2" type="tel" name="phone3" required maxLength="4" />
                            </dd>
                        </dl>
                        <dl>
                            <dt><label htmlFor="address">주소</label><span className="req">필수</span></dt>
                            <dd>
                            <input className="f_input2 w_full" type="text" name="address" id="address" required />
                            </dd>
                        </dl>
                        <dl>
                            <dt><label htmlFor="id">아이디</label><span className="req">필수</span></dt>
                            <dd>
                            <input className="f_input2 w_full" type="text" name="id" id="id" required />
                            </dd>
                        </dl>
                        <dl>
                            <dt><label htmlFor="oldPassword">기존 암호</label><span className="req">필수</span></dt>
                            <dd>
                            <input className="f_input2 w_full" type="password" name="oldPassword" id="oldPassword" defaultValue={oldPassword} onChange={e => setOldPassword(e.target.value)} required />
                            </dd>
                        </dl>
                        <dl>
                            <dt><label htmlFor="newPassword">신규 암호</label><span className="req">필수</span></dt>
                            <dd>
                            <input className="f_input2 w_full" type="password" name="newPassword" id="newPassword" defaultValue={newPassword} onChange={e => setNewPassword(e.target.value)} required />
                            </dd>
                        </dl>
                            {/* <!-- 버튼영역 --> */}
                            <div className="board_btn_area">
                                <div className="left_col btn1">
                                    <button className="btn btn_skyblue_h46 w_100"
                                        onClick={() => updateAdminPassword()}>가입</button>
                                </div>
                            </div>
                            {/* <!--// 버튼영역 --> */}
                        </div>
                        {/* <!--// 본문 --> */}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default EgovAdminPasswordUpdate;
